mapper.sql:
SELECT
	ID,
	DISTRIBUTED_TRANSACTION_MAIN_ID,
	INVERSE_OPER,
	INVERSE_OPER_TYPE,
	TRANS_TABLE_NAME,
	TRANS_PRIMARY_KEY_VAL,
	TRANSACTION_STATUS,
	CREATE_DATE
FROM DISTRIBUTED_TRANSACTION_PART

	AND Id=#{id} 
	AND DISTRIBUTED_TRANSACTION_MAIN_ID=#{distributedTransactionMainId} 
	AND INVERSE_OPER_TYPE=#{inverseOperType} 
	AND TRANS_TABLE_NAME=#{transTableName} 
	AND TRANS_PRIMARY_KEY_VAL=#{transPrimaryKeyVal} 
	AND TRANSACTION_STATUS=#{transactionStatus}
